import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

import PrestatairesService from "../services/prestataires.service";
import BilletterieService from "@/services/billetterie.service";

export default new Vuex.Store({
  state: () => ({
    prestataires: [],
    commande : 0,
  }),
  getters: {},
  mutations: {
    updatePrestataires: (state, prestataires) => {
      state.prestataires = prestataires;
    },
    updateCommande: (state, reponse) => {
      state.commande = reponse;
    }
  },
  actions: {
    async getAllPrestataires({ commit }) {
      console.log("récupération des prestataires");
      let response = await PrestatairesService.getAllPrestataires();
      if (response.error === 0) {
        commit("updatePrestataires", response.data);
      } else {
        console.log(response.data);
      }
    },

    async achatBilletterie({commit}, data){
      let response = await BilletterieService.submitForm();
      if (response.error === 0) {
        commit("updateCommande", response.error);
      } else {
        commit("updateCommande", response.error);
        console.log(response.data);
      }
    }
  },
  modules: {},
});
