import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

import PrestatairesService from "../services/prestataires.service";

export default new Vuex.Store({
  state: () => ({
    prestataires: [],
    utilisateur: {
      role: '',
      nom: '',
    },
  }),
  getters: {},
  mutations: {
    updatePrestataires: (state, prestataires) => {
      state.prestataires = prestataires;
    },
    SET_USER(state, user, role) {
      state.utilisateur.nom = user;
      state.utilisateur.role = role;
    },
    LOGOUT(state) {
      state.utilisateur = {
        role: '',
        nom: ''
      };
    }
  },
  actions: {
    async getAllPrestataires({ commit }) {
      console.log("récupération des prestataires");
      let response = await PrestatairesService.getAllPrestataires();
      if (response.error === 0) {
        commit("updatePrestataires", response.data);
      } else {
        console.log(response.data);
      }
    },
    logout({ commit }) {
      commit('LOGOUT');
    },
     async logIn({ commit }, data){
       console.log(data.nom)
       console.log(data.role)
      commit('SET_USER', data.nom, data.role)
    }
  },
  modules: {},
});
