import LocalSource from "@/datasource/controller";

async function submitForm() {
    try {
        //const response = await axios.post('/submit-ticket', this.form);
        const response = await LocalSource.insertBillet();

        // Gérer la réponse en cas de succès
        this.successMessage = "Réservation réussie ! Merci de votre achat.";
        this.errorMessage = '';

        // Optionnel : réinitialiser le formulaire
        this.form = {
            firstName: '',
            lastName: '',
            email: '',
            ticketType: '',
            ticketQuantity: '',
            date: ''
        };
    } catch (error) {
        // Gérer les erreurs
        this.successMessage = '';
        this.errorMessage = "Une erreur est survenue lors de la réservation.";
    }
}

export default {
    submitForm,
}
